(function () {
  var variable = Agtk.variables.get(Agtk.variables.getIdByName('StaticCounter001'));
  var value = variable.getValue();

  if (value < 0) {
    Agtk.log('No initial static data, setting counter');
    variable.setValue(0);
  } else {
    Agtk.log('Stored Value: ' + value);
  }
})()

